mapper.select:
SELECT

FROM bs_view a

WHERE a.id = #{id}

SELECT

FROM bs_view a


  a.del_flag = #{DEL_FLAG_NORMAL}

    AND a.post_id LIKE
    '%'||#{postId}||'%'
    '%'+#{postId}+'%'
    concat('%',#{postId},'%')


    AND a.create_by = #{createBy.id}




    ORDER BY ${page.orderBy}


    ORDER BY a.update_date DESC

mapper.update:
UPDATE bs_view SET
	post_id = #{postId},
	update_by = #{updateBy.id},
	update_date = #{updateDate},
	remarks = #{remarks}
WHERE id = #{id}

UPDATE bs_view SET 
	del_flag = '0'
WHERE id = #{id}
